mapper.select:
select * from employee


        AND name like concat('%',#{name},'%')


        AND gender = #{gender}


        AND entry_date BETWEEN #{date1} AND #{date2}

mapper.update:
update `employee` set username = #{username}, name = #{name}, gender = #{gender}, image = #{image}, job = #{job},
                      entry_date = #{entryDate}, dept_id = #{deptId}
where id = #{id}
